import pytest
from problems.longest_palindromic import Solution


@pytest.mark.parametrize("s,expected", [
    ("", ""),
    ("a", "a"),
    ("aa", "aa"),
    ("aaa", "aaa"),
    ("aba", "aba"),
    ("abaa", "aba"),
    ("abaac", "aba"),
    ("aaba", "aba"),
    ("caaba", "aba"),
    ("abba", "abba"),
    ("abcba", "abcba"),
    ("abcac", "cac"),
    ("", ""),
])
def test(s, expected):
    assert Solution().longestPalindrome(s) == expected
