import pytest
from problems.wildcard_matching import Solution


@pytest.mark.parametrize("s, p, expected", [
    ("", "", True),
    ("", "*", True),
    ("", "**", True),
    ("a", "*", True),
    ("ab", "*", True),
    ("ab", "a*", True),
    ("ab", "*b", True),
    ("ab", "a*b", True),
    ("ab", "a*bc", False),
    ("ab", "a*cb", False),
    ("ab", "ac*b", False),
    ("ab", "ca*b", False),
    ("ab", "a?", True),
    ("a", "a?", False),
    (
    "",
    "**",
    False),
])
def test_isMatch(s, p, expected):
    assert Solution().isMatch(s, p) == expected
